mapper.select:
SELECT
    b.*, h.`name` as housingName,
    r.address as roominfoAddress,r.owner_card as ownerCard, r.owner_name as ownerName, r.owner_phone as ownerPhone
FROM
    billaccount b
JOIN housinginfo h ON b.housing_id = h.id
JOIN roominfo r ON b.roominfo_id = r.id

    and b.deleteStatus = false and h.deleteStatus = false and r.deleteStatus = false

        and h.`code` like CONCAT(#{code},'%')


        and b.housing_id = #{housingId}


        and b.roominfo_id = #{roominfoId}


        and b.cost_type = #{costType}


        and b.status = #{status}


        and b.payStatus = #{paystatus}


        and b.id = #{id}


        limit #{currPage},#{pageSize}

SELECT count(DISTINCT b.housing_id) as userNum, sum(b.bill_entry_amount) as sumAmount FROM billaccount b join housinginfo h on b.housing_id = h.id
AND b.housing_id = #{housingId}
AND h.parent_id = #{propertyId}
AND date_format(b.payDate, '%Y-%m-%d') = date_format(now(), '%Y-%m-%d') AND b.payStatus = true

SELECT payStatus , sum(b.bill_entry_amount) AS sumAmount FROM billaccount b JOIN housinginfo h ON b.housing_id = h.id
and b.deleteStatus = false and h.deleteStatus = false and h.`code` like CONCAT(#{code},'%')
GROUP BY b.payStatus;
